SELECT Coursename, grade FROM CourseOfferRegistrations, Courses WHERE StudentId = %1% AND DegreeId = %2% AND CourseOfferRegistrations.CourseId = Courses.CourseId ORDER BY year, quartile, CourseOfferID;
