SELECT StudentRegistrationsToDegrees.DegreeId, CAST(count(*) filter (WHERE Students.Gender = 'F') AS FLOAT)*100 / CAST(count(*) AS FLOAT) as percentage FROM StudentRegistrationsToDegrees, Students WHERE StudentRegistrationsToDegrees.StudentId = Students.StudentId AND NOT EXISTS (SELECT * FROM completeddegrees WHERE completeddegrees.StudentRegistrationId = StudentRegistrationsToDegrees.StudentRegistrationId) GROUP BY StudentRegistrationsToDegrees.DegreeId ORDER BY StudentRegistrationsToDegrees.DegreeId;
WITH StudentsinDegree AS (SELECT distinct students.studentid, students.gender from students, studentregistrationstodegrees, degrees where students.studentid = studentRegistrationsToDegrees.studentid AND studentregistrationstodegrees.degreeid = degrees.degreeid AND Degrees.Dept = %1%) SELECT 100.0 * (SUM(CASE WHEN Gender='F' THEN 1 ELSE 0 END)::DECIMAL / COUNT(studentsindegree.studentid)) as percentage FROM studentsindegree;
WITH nonnull AS (select courseofferid,grade from courseofferregistrations where grade is not null) select courseid, SUM(CASE WHEN nonnull.grade >= %1% THEN 1 ELSE 0 END)::DECIMAL * 100 / count(nonnull.grade) AS percentagePassing FROM nonnull, courseofferregistrations WHERE nonnull.courseofferid = courseofferregistrations.courseofferid GROUP BY courseid ORDER BY courseid;
WITH max_grade_pc_1_2018 AS (SELECT CR.courseofferid, max(CR.grade) AS max_grade FROM courseofferregistrations COR WHERE COR.year = 2018 AND COR.quartile =1 GROUP BY COR.courseofferid), counts_excellent AS (SELECT studentID, count(courseofferregistrations.courseofferid) as counts FROM max_grade_pc_1_2018, courseofferregistrations, StudentRegistrationsToDegrees WHERE max_grade_pc_1_2018.courseofferid = courseofferregistrations.courseofferid AND max_grade_pc_1_2018.max_grade = courseofferregistrations.grade AND courseofferregistrations.studentregistrationid = studentregistrationstodegrees.studentregistrationid GROUP BY studentid) SELECT studentID, counts FROM counts_excellent WHERE counts >= %1%;
