SELECT distinct co.CourseOfferId, co.CourseId, co.Year, co.Quartile, c.CourseName, c.CourseDescription, c.DegreeId, c.ECTS, d.Dept, d.DegreeDescription, d.TotalECTS, t.TeacherId, t.TeacherName, t.Address, t.BirthyearTeacher, t.Gender FROM CourseOfferRegistrations co, Courses c, Degrees d, TeacherAssignmentsToCourses tac, Teachers t WHERE co.CourseOfferId = 1 AND co.CourseId = c.CourseId AND d.DegreeId = c.DegreeId AND co.CourseOfferId = tac.CourseOfferId AND tac.TeacherId = t.TeacherId;
SELECT Distinct CourseOfferRegistrations.courseOfferId, CourseId, Year, Quartile, Students.*, Degrees.* FROM StudentRegistrationsToDegrees, Students, Degrees, CourseOfferRegistrations, StudentAssistants WHERE StudentRegistrationsToDegrees.StudentRegistrationId = 3 AND StudentRegistrationsToDegrees.StudentId = Students.StudentId AND StudentRegistrationsToDegrees.DegreeId = Degrees.DegreeId AND StudentAssistants.StudentRegistrationId = 3 AND CourseOfferRegistrations.CourseOfferId = StudentAssistants.CourseOfferId;
SELECT AVG(Grade) FROM CourseOfferRegistrations WHERE StudentRegistrationId = 3;
